export default class AuthService {
    constructor() {
        this.loggedIn = false;
        this.isAuthenticated = async () => {
            const promise = new Promise((resolve, reject) => {
                setTimeout(() => resolve(this.loggedIn), 800);
            });
            return promise;
        };
        this.login = async () => (this.loggedIn = true);
        this.logout = async () => (this.loggedIn = false);
    }
}
